import React from 'react'


const urls = [];
const baseURL ="https://maps.googleapis.com/maps/api/geocode/json?latlng=40.714224,-73.961452&key=AIzaSyD8g2ang82_R7aQG8AdVAJg4jkQ52-cp4E";
const nations = [];
const uniqueNations = [];
const dt = [];
var obj = {};

export default class Nation extends React.Component {

    constructor(props)
    {
        super(props);

       this.state = { 
        
            loading: true,
            data: []
        };

        
        
    }


    

    /*
    state = {
      loading: true,
      person: null
    };
  
    async componentDidMount() {
      const url = "https://maps.googleapis.com/maps/api/geocode/json?latlng=40.714224,-73.961452&key=AIzaSyD8g2ang82_R7aQG8AdVAJg4jkQ52-cp4E";
      const response = await fetch(url);
      const data = await response.json();

      this.setState({ person: data.results[0].address_components[6], loading: false });
    }
  
    render() 
    {
      if (this.state.loading) {
        return <div>loading...</div>;
      }
  
      if (!this.state.person) {
        return <div>didn't get a person</div>;
      }
  
      return (
        <div>
          <div>{this.state.person.long_name}</div>
          <div>{this.props.data.downloads[1].app_id}.</div>
        </div>
      );
    }

    */

componentDidMount() 
{
    //Create the query for each pair of longitude&latitude
    this.props.data.downloads.map(download => (
        // Query too Goolgle Maps API in order to get the nation
        urls.push("https://maps.googleapis.com/maps/api/geocode/json?latlng="+ download.latitude + ","+ download.longitude + "&result_type=country&key=AIzaSyD8g2ang82_R7aQG8AdVAJg4jkQ52-cp4E")
    ))  

    //Send request to the API
    var promises = Promise.all(urls.map(url => (
        fetch(url)
    )));

//this.setState({nations: res.results[0].address_components[6] , loading: false})

    //Get results of the API and save the values
    promises
        .then((results) =>
            Promise.all(results.map(r => r.json()))
        )
        .then((res) =>
            res.map(t =>          
                {
                    dt.push(t.results[0].address_components[0].long_name)
                
                   // console.log("hello")
                   // console.log(t.results[0].address_components[0].long_name)
                }
                
            ))
            .then(    this.setState({loading: false, data: dt}))
           // res.map(
             //    t =>( 
                     
               //     nations.push(t.results[0].address_components[0].long_name)
                  
                    //console.log(t.results[0].address_components[0].long_name)
                //this.setState({nations: res.results[0].address_components[6] , loading: false})
                
                
              //      )
                    
        
            
      //  ) 
      //  .then(
      //      this.setState({loading: false})
      //  )
        
}



    render()
    {
        /*
        // The urls that query the Google API
        this.props.data.downloads.map(download => (
            urls.push("https://maps.googleapis.com/maps/api/geocode/json?latlng="+ download.longitude + ","+ download.latitude + "&key=AIzaSyD8g2ang82_R7aQG8AdVAJg4jkQ52-cp4E")
        ))
        
        
        var promises = Promise.all(urls.map(url => (
            fetch(url)
        )))

        promises
            .then((results) =>
                Promise.all(results.map(r => r.json()))
            )
            .then((res) =>(
                res.map(n => 
                    (

                     console.log(n.results)
                        
                    ))
            ))
        */
    


            /*
            promises
            .then((results) =>
                Promise.all(results.map(r => r.json()
                    ))
            )
            .then((res) => (res.map(n =>(

            ))
            ))*/


        
           
      if (this.state.loading) 
      {
        return <div>loading...</div>;
      }
  
      if (!this.state.data) {
        return <div>didn't get the nations</div>;
      }
  
      let itemList = this.state.data.map(
               // t => {

                 //   console.log("hello")
                   // console.log(t)
                //}

          function(item)
          {
                console.log("hello")
                //console.log(item)
                 return <p>{item}</p>
          }

          //res.map(
            //    t =>( 
                    
              //     nations.push(t.results[0].address_components[0].long_name)

      );

    console.log(this.state.data);

      const result = [];
      const map = new Map();
      for (const item of this.state.data) {
          console.log("hello");
          console.log(item);
          if(!map.has(item)){
              map.set(item, true);    // set any value to Map
              result.push(
                  item
               );
          }
      }
      console.log(result);

      //Counting the number of repetition

      let count = 0;
      let nationDownload = result.map(function(item)
      {
          count = 0;
          for(it in this.state.data)
          {
              if(item == it)
              {
                  count = count + 1;
              }
          }

        return <li key={item}>{count}</li>
      })


      return (
          <div>
                {itemList}
          </div>
      )
        
        /*
        if (this.state.loading) {
        return <div>loading...</div>;
      }
  
        if (!this.state.person) {
            return <div>didn't get a person</div>;
        }
  
        return (
            <div>
            <div>{this.state.person.long_name}</div>
            <div>{this.props.data.downloads[1].app_id}.</div>
            </div>
        );*/
    }
}
